SELECT product_id FROM `rifle_scopes` WHERE brand IN ('CVLIFE', 'UTG')
ORDER BY price
LIMIT 1;

SELECT product_id FROM `rifle_scopes` WHERE brand != 'Vortex Optics' AND reticle != 'P4'
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `rifle_scopes` WHERE brand IN ('Simmons', 'NcSTAR') 
AND price < (SELECT AVG(price) FROM rifle_scopes WHERE brand IN ('Simmons', 'NcSTAR')) 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `rifle_scopes` WHERE brand IN ('AIM Sports', 'Simmons') 
AND reticle IN ('Crosshairs', 'Illuminated') 
LIMIT 1;

SELECT product_id FROM `rifle_scopes` WHERE brand != 'Bushnell' AND reticle != 'Crosshairs'
ORDER BY price ASC
LIMIT 1;

SELECT product_id FROM `rifle_scopes` WHERE brand IN ('Bushnell', 'Simmons') 
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `rifle_scopes` WHERE brand IN ('AIM Sports', 'Vortex Optics') 
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `rifle_scopes` WHERE brand IN ('UTG', 'Simmons') 
AND waterproof = 'no' 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `rifle_scopes` WHERE price < 30 AND (reticle = 'Illuminated' OR reticle = 'P4') 
ORDER BY price ASC 
LIMIT 1;

