SELECT product_id FROM `handheld_flashlights` WHERE brand IN ('ThruNite', 'NEBO') 
ORDER BY beam_distance DESC 
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE battery_life >= 5 
ORDER BY beam_distance DESC
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE price <= 10
ORDER BY price ASC
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE battery_life > (SELECT AVG(battery_life) FROM handheld_flashlights) 
AND beam_distance >= 80 
ORDER BY price ASC LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE battery_type = 'AAA' 
ORDER BY beam_distance DESC, battery_life DESC 
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE brand IN ('TaoTronics', 'ThruNite') 
ORDER BY price 
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE brand IN ('MagLite', 'Eton') 
ORDER BY product_id
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE battery_type = 'AA' AND battery_life > 10 
ORDER BY price ASC 
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE beam_distance > (SELECT AVG(beam_distance) FROM handheld_flashlights)
ORDER BY price ASC
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE brand IN ('Amerelle', 'DEWALT') 
ORDER BY battery_life DESC 
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE brand IN ('Streamlight', 'Eton') 
ORDER BY beam_distance DESC 
LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE brand IN ('TerraLUX', 'SureFire') 
AND battery_life >= (SELECT AVG(battery_life) FROM handheld_flashlights WHERE brand IN ('TerraLUX', 'SureFire')) 
ORDER BY beam_distance DESC LIMIT 1;

SELECT product_id FROM `handheld_flashlights` WHERE price <= 30 AND beam_distance >= 1000
ORDER BY beam_distance DESC, price ASC
LIMIT 1;
